#!/usr/bin/env node

// build-env.js - Gera arquivo com variáveis de ambiente do Vercel
const fs = require('fs');
const path = require('path');

const envContent = `
// Auto-generated by build-env.js - DO NOT EDIT
window.__ENV__ = {
  VITE_SUPABASE_URL: '${process.env.VITE_SUPABASE_URL || ''}',
  VITE_SUPABASE_KEY: '${process.env.VITE_SUPABASE_KEY || ''}'
};
`;

const outputPath = path.join(__dirname, 'js', 'env.js');

fs.writeFileSync(outputPath, envContent.trim());
console.log('✅ Variáveis de ambiente geradas em js/env.js');
